mapper.select:
select product_id, product_name, price, quantity
from product
where product_id = #{productId}

select product_id, product_name, price, quantity from product
product_name like concat('%', #{productName}, '%')
and price <= #{maxPrice}

mapper.update:
update product
product_name = #{productName}, price = #{price}, quantity = #{quantity},
where product_id = #{productId}
